mapper.update:
UPDATE
	tbl_thumbImg
SET
	bti_url = #{uploadName}
WHERE
	bti_ref = #{bl_uid}

UPDATE
	tbl_blockTags
SET
	bt_name = #{tag}
WHERE
	bt_ref = #{bl_uid}

UPDATE
	tbl_blpath
SET
	bp_path = #{blockPathDTO.bp_path}, bp_date = sysdate, bp_browstype = #{blockPathDTO.bp_browstype}, bp_booktype = #{blockPathDTO.bp_booktype}
WHERE
	bp_iuid = #{session_iuid}
